    public static void main(String[] args) throws IOException {
        String s = ImageUtils.processImage(HtmlText.CONTEXT, HtmlText.URL);
        String context = HtmlText.WORD_HEAD + s + HtmlText.WORD_FOOT;

        //这里是必须要设置编码的，不然导出中文就会乱码。
        byte[] b = context.getBytes(StandardCharsets.UTF_8);
        //将字节数组包装到流中
        InputStream inputStream = new ByteArrayInputStream(b);
        long l = System.currentTimeMillis();
        OutputStream outputStream3 = Files.newOutputStream(Paths.get("d:/blue_word2.docx"));
        Html2DocxIceBlueConverter blueConvert = new Html2DocxIceBlueConverter(context, outputStream3);
        l = System.currentTimeMillis();
        blueConvert.convert();
        log.info("ice-blue方式导出用时:{}ms", System.currentTimeMillis() - l);

//        postConvertRemove1stLine("d:/blue_word1.docx");
    }

